import React from 'react'
import ReactDOM from 'react-dom'

var array1 = []

const Note = ({note, id, exercises }) => {
  return (
    <p>{note}   {exercises}</p>
  )
}
const AddPart = ({part}) => {
    array1.concat({part})
}


const Course = (props) => {

  const reducer = (accumulator, currentValue) => accumulator + currentValue

  const rows = () => props.course.parts.map(parts => 
    <Note 
      key={parts.id}
      note={parts.name}
      exercises={parts.exercises}
    />
  )
  var total = props.course.parts.map(parts => parts.exercises)
  total = total.reduce(reducer)
  return(
  <div>
    <h1>{props.course.name}</h1>
    {rows()}
    <p>total: {total}</p>

  </div>
  )
}

const App = () => {
  const course = {
    name: 'Half Stack application development',
    parts: [
      {
        name: 'Fundamentals of React',
        exercises: 10,
        id: 1
      },
      {
        name: 'Using props to pass data',
        exercises: 7,
        id: 2
      },
      {
        name: 'State of a component',
        exercises: 14,
        id: 3
      }
    ]
  }

  return (
    <div>
      <Course course={course} />
    </div>
  )
}

ReactDOM.render(
  <App />, document.getElementById('root')
)
